function getIconHelper() {
  const cache = new Map<string, ReactNode>();
  const dirCache = new Map<string, ReactNode>();

  cache.set("js", icons.javascript);
  cache.set("jsx", icons.react);
  cache.set("tsx", icons.react);
  cache.set("ts", icons.typescript);

  cache.set("css", icons.css);
  cache.set("json", icons.json);
  cache.set("html", icons.html);
  cache.set("png", icons.image);
  cache.set("jpg", icons.image);
  cache.set("Dockerfile", icons.docker);
  cache.set("md", icons.markdown);
  cache.set("mdx", icons.markdown);
  cache.set(".gitignore", icons.git);
  cache.set(".git", icons.git);
  cache.set(".eslintrc", icons.eslint);
  cache.set(".eslintrc.js", icons.eslint);
  cache.set(".eslintrc.ts", icons.eslint);
  cache.set("bun.lockb", icons.bun);
  cache.set(".eslintrc.cjs", icons.eslint);
  cache.set(".eslintrc.mjs", icons.eslint);

  dirCache.set("src/open", icons.srcOpen);
  dirCache.set("src/close", icons.srcClosed);
  dirCache.set("assets/open", icons.folderAssetsOpen);
  dirCache.set("assets/close", icons.folderAssetsClose);
  dirCache.set("github/open", icons.folderGithubOpen);
  dirCache.set("github/close", icons.folderGithubClose);
  dirCache.set("node_modules/close", icons.folderNodeClose);
  dirCache.set("node_modules/open", icons.folderNodeOpen);

  cache.set("ico", icons.favico);

  return function (
    extension: string,
    fullName: string,
    dir?: { isDir: boolean; open: boolean }
  ): ReactNode {
    if (dir && dir.isDir) {
      const key = `${fullName}/${dir.open ? "open" : "close"}`;
      if (dirCache.has(key)) {
        return dirCache.get(key);
      }

      return dir.open ? icons.defaultFolderOpen : icons.defaultFolderClose;
    }

    if (cache.has(fullName)) return cache.get(fullName);
    else if (cache.has(extension)) return cache.get(extension);
    else return icons.file;
  };
}

export const getIcon = getIconHelper();
